class Solution {
    public boolean T(List<Integer> al, int target, int idx, Boolean dp[][]) {
        if(idx==al.size()){
            if(target==0) return true;
            return false;
        }
        if(dp[idx][target]!=null) return dp[idx][target];
        boolean ex = T(al, target, idx+1, dp);
        boolean in = false;
        if(al.get(idx)<=target){
            in = T(al, target-al.get(idx), idx+1, dp);
        }
        return dp[idx][target] =ex || in;
    }
    public boolean is(int nums[], int [][]q, int k){
        int n = nums.length;
        // k = q.length;
        List<List<Integer>> al = new ArrayList<>();
        for(int i=0; i<=n; i++) {
            al.add(new ArrayList<>());
        }
        // System.out.pritln
        for(int i=0; i<k; i++) {
            al.get(q[i][0]).add(q[i][2]);
            al.get(q[i][1]+1).add(-q[i][2]);
        }
        int sum = 0;
       // System.out.println(al);
        HashMap<Integer, Integer> map = new HashMap<>();
        for(int i=0; i<n; i++) {
            for(int el : al.get(i)){
                if(el<0){
                    map.put(-el, map.get(-el)-1);
                }else{
                    map.put(el, map.getOrDefault(el, 0)+1);
                }
            }
            List<Integer> temp = new ArrayList<>();
            for(int key : map.keySet()) {
                for(int j=0; j<map.get(key); j++){
                    temp.add(key);
                }
            }
            Boolean dp[][] = new Boolean[temp.size()][nums[i]+1];
            if(!T(temp, nums[i], 0, dp)) return false;
        }
        return true;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int s=0, e=queries.length, ans=-1;
        while(s<=e){
            int m = s+(e-s)/2;
            if(is(nums, queries, m)){
                ans = m;
                e = m-1;
            }else{
                s = m+1;
            }
        }
        return ans;
    }
}

