class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        int res = 0;
        for (int i = 0; i < n; i++) {
            int num = nums[i];
            if (num == 0) {
                continue;
            }
            boolean[] dp = new boolean[num + 1];
            dp[num] = true;
            for (int j = 0; j < m; j++) {
                if (queries[j][0] > i || queries[j][1] < i) {
                    continue;
                }
                for (int k = 0; k <= num; k++) {
                    if (dp[k] && k >= queries[j][2]) {
                        dp[k - queries[j][2]] = true;
                    }
                }
                if (dp[0]) {
                    res = Math.max(res, j + 1);
                    break;
                }
            }
            if (!dp[0]) {
                return -1;
            }
        }
        return res;
    }
}

