        n = len(nums)
        q = len(queries)
        ans = 0 
        for k, x in enumerate(nums): 
            dp = [[inf]*(x+1) for _ in range(q+1)] # dp[0][x]
            dp[q][0] = q
            for i in range(q-1, -1, -1): 
                dp[i][0] = i 
                l, r, v = queries[i]
                for j in range(1, x+1): 
                    dp[i][j] = dp[i+1][j]
                    if l <= k <= r and v <= j: dp[i][j] = min(dp[i][j], dp[i+1][j-v])
            if dp[0][x] == inf: return -1 
            ans = max(ans, dp[0][x])
        return ans

