class Solution
{
    public int minZeroArray(int[] as, int[][] qs)
    {
        if (Arrays.stream(as).max().orElse(0) == 0)
        {
            return 0;
        }
        int n = as.length;
        Integer[] table = new Integer[n];
        for (int i = 0; i < n; i++)
        {
            if (as[i] == 0)
            {
                table[i] = 0;
            }
        }
        Map<Integer, Set<Integer>> map = new HashMap<>();
        for (int j = 0; j < qs.length; j++)
        {
            int[] q = qs[j];
            for (int i = q[0]; i <= q[1]; i++)
            {
                if (table[i] != null)
                {
                    continue;
                }
                Set<Integer> set1 = map.getOrDefault(i, new HashSet<>());
                Set<Integer> set2 = new HashSet<>(set1);
                for (int curr : set1)
                {
                    set2.add(curr + q[2]);
                }
                set2.add(q[2]);
                if (set2.contains(as[i]))
                {
                    table[i] = j + 1;
                }
                map.put(i, set2);
            }
        }
        int k = 0;
        for (Integer need : table)
        {
            if (need == null)
            {
                return -1;
            }
            k = Math.max(k, need);
        }
        return k;
    }
}

