class Solution(object):
    def dfs(self, target, queries, i, start, m):
        #print(target, i, start)
        if (target,start) in m:
            return m[(target,start)]
        if start >= len(queries) and target > 0:
            return -1
        if target == 0:
            return start

        if queries[start][0] > i or queries[start][1] < i:
            m[(target,start)] = self.dfs(target, queries, i, start+1, m)
            return m[(target,start)]

        if queries[start][2] > target:
            m[(target,start)] = self.dfs(target, queries, i, start+1, m)
            return m[(target,start)]

        tmp1 = self.dfs(target-queries[start][2], queries, i, start+1, m)
        tmp2 = self.dfs(target, queries, i, start+1, m)
        if tmp1 == -1:
            m[(target,start)] = tmp2
            return tmp2
        if tmp2 == -1:
            m[(target,start)] = tmp1
            return tmp1
        m[(target,start)] = min(tmp1,tmp2)
        return m[(target,start)]

    def minZeroArray(self, nums, queries):
        """
        :type nums: List[int]
        :type queries: List[List[int]]
        :rtype: int
        """
        finalResult = 0
        for i in range(0, len(nums)):
            m = {}
            k = self.dfs(nums[i], queries, i, 0, m)
            if k == -1:
                return -1
            finalResult = max(finalResult, k)

        return finalResult

