class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        int max_k = 0;

        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) continue;

            vector<pair<int, int>> qs;
            for (int j = 0; j < q; ++j) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                if (l <= i && i <= r) {
                    qs.emplace_back(j, val);
                }
            }

            int sum_total = 0;
            for (auto& p : qs) sum_total += p.second;
            if (sum_total < target) return -1;

            unordered_set<int> possible_sums = {0};
            int sum_so_far = 0;
            bool found = false;

            for (auto& p : qs) {
                int q_idx = p.first;
                int val = p.second;
                sum_so_far += val;

                unordered_set<int> new_sums;
                bool break_loop = false;

                for (int s : possible_sums) {
                    int new_s_val = s + val;
                    if (new_s_val == target) {
                        max_k = max(max_k, q_idx + 1);
                        found = true;
                        break_loop = true;
                        break;
                    } else if (new_s_val < target) {
                        new_sums.insert(new_s_val);
                    }
                }

                if (break_loop) break;

                for (int s : new_sums) possible_sums.insert(s);

                if (sum_so_far >= target && possible_sums.count(target)) {
                    max_k = max(max_k, q_idx + 1);
                    found = true;
                    break;
                }
            }

            if (!found) return -1;
        }

        return max_k;
    }
};

