class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        if all(nums[i] == 0 for i in range(n)): return 0 # all zero
        dp = [[False]*(nums[i]+1) for i in range(n)]
        for i in range(n):
            dp[i][0] = True
        for k, (l, r, val) in enumerate(queries, start=1):
            for i in range(l, r+1):
                for s in range(nums[i]-val, -1, -1):
                    if dp[i][s]:
                        dp[i][s+val] = True
            if all(dp[i][nums[i]] for i in range(n)):
                return k
        return -1

