class Solution {

    private boolean check(int[] nums, int[][] queries, int limit) {
        boolean[] dp = new boolean[1010];
        for (int i = 0;i < nums.length;i ++) {
            int target = nums[i];
            Arrays.fill(dp, false);
            dp[0] = true;
            boolean find = false;
            for (int j = 0;j <= limit;j ++) {
                int l = queries[j][0], r = queries[j][1], updateVal = queries[j][2];
                if (l <= i && i <= r) {
                    for (int val = target;val >= updateVal;val --) {
                        if (dp[val - updateVal]) {
                            dp[val] = true;
                        }
                        if (dp[target]) {
                            break;
                        }
                    }
                }
                if (dp[target] == true) {
                    find = true;
                    break;
                }
            }
            if (!find) {
                return false;
            }
        }
        return true;
    }

    private boolean allZeros(int[] nums) {
        for (int val : nums) {
            if (val != 0) {
                return false;
            }
        }
        return true;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        if (allZeros(nums)) {
            return 0;
        }
        int l = 0, r = queries.length;
        while (l < r) {
            int mid = (l + r) / 2;
            if (check(nums, queries, mid)) {
                r = mid;
            } else {
                l = mid + 1;
            }
        }
        if (l == queries.length) {
            return - 1;
        } else {
            return l + 1;
        }
    }

}

