class Solution {
public:


    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // Check if the array is already zero
        if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) {
            return 0;
        }

        int n = nums.size();
        int q_size = queries.size();

        vector<vector<pair<int, int>>> Q(n); // (original index, val)
        vector<vector<int>> original_indices(n);
        vector<vector<bool>> can_reach(n);

        for (int j = 0; j < n; ++j) {
            for (int i = 0; i < q_size; ++i) {
                auto& q = queries[i];
                int l = q[0], r = q[1], val = q[2];
                if (l <= j && j <= r) {
                    Q[j].emplace_back(i, val);
                }
            }
            sort(Q[j].begin(), Q[j].end(), [](const pair<int, int>& a, const pair<int, int>& b) {
                return a.first < b.first;
            });
            original_indices[j].clear();
            for (auto& p : Q[j]) {
                original_indices[j].push_back(p.first);
            }
            can_reach[j].resize(Q[j].size() + 1, false);
            unordered_set<int> current_sums;
            current_sums.insert(0);
            can_reach[j][0] = (current_sums.count(nums[j]) > 0);
            for (int m = 1; m <= Q[j].size(); ++m) {
                int val = Q[j][m-1].second;
                unordered_set<int> new_sums(current_sums.begin(), current_sums.end());
                for (int s : current_sums) {
                    int new_s = s + val;
                    if (new_s <= nums[j]) {
                        new_sums.insert(new_s);
                    }
                }
                current_sums = new_sums;
                can_reach[j][m] = current_sums.count(nums[j]) > 0;
            }
        }

        for (int original_index = 0; original_index < q_size; ++original_index) {
            bool all_ok = true;
            for (int j = 0; j < n; ++j) {
                if (Q[j].empty()) {
                    if (nums[j] != 0) {
                        all_ok = false;
                        break;
                    }
                    continue;
                }
                auto& indices = original_indices[j];
                auto it = upper_bound(indices.begin(), indices.end(), original_index);
                int m_j = it - indices.begin();
                if (!can_reach[j][m_j]) {
                    all_ok = false;
                    break;
                }
            }
            if (all_ok) {
                return original_index + 1;
            }
        }

        return -1;
    }
};

