class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(),m = queries.size();
        if(accumulate(nums.begin(),nums.end(),0ll)==0)return 0;
        auto f = [&](int index,int value,vector<vector<int>>&dp,int x,int idx,auto &&self)->bool{
            if(index==x){
                return value==0?true:false;
            }
            if(dp[index][value]!=-1)return dp[index][value];
            bool res = self(index+1,value,dp,x,idx,self);
            if(res==true)return true;
            int l = queries[index][0],r = queries[index][1],val = queries[index][2];
            if(idx>=l && idx<=r && val<=value){
                res = (res | self(index+1,value-val,dp,x,idx,self));
            }
            return dp[index][value] = res;
        };
        auto isPossible = [&](int x,int value,int idx)->bool{
            vector<vector<int>>dp(x+1,vector<int>(value+1,-1));
            return f(0,value,dp,x,idx,f);
        };
        auto check = [&](int value,int idx){
            int start = 0,end = m,ans = -1;
            while(start<=end){
                int mid = (start + (end-start)/2);
                if(isPossible(mid,value,idx)){
                    ans = mid;
                    end = mid-1;
                }
                else start = mid+1;
            }
            return ans;
        };
        int maxi = 0;
        for(int i=0;i<n;i++){
            int x = check(nums[i],i);
            if(x==-1)return -1;
            maxi = max(maxi,x);
        }
        return maxi;
    }
};

