class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        mods = [[] for _ in range(n + 1)]
        l = 0
        r = len(queries) + 1
        modend = 0
        def check(nums, mods):
            c = []
            for i, v in enumerate(nums):
                for j in mods[i]:
                    if j > 0:
                        c.append(j)
                    else:
                        c.remove(-j)
                if not c or len(c) == 0:
                    if v != 0:
                        return False
                n = len(c)
                b = [[False for _ in range(v + 1)] for _ in range(n + 1)]
                for i in range(n + 1):
                    b[i][0] = True
                for i in range(1, v + 1):
                    b[0][i] = False
                for i in range(1, n + 1):
                    for j in range(1, v + 1):
                        if j < c[i - 1]:
                            b[i][j] = b[i - 1][j]
                        if j >= c[i - 1]:
                            b[i][j] = (b[i - 1][j] or b[i - 1][j - c[i - 1]])
                if not b[n][v]:
                    return False
            return True


        while l < r:
            m = (l + r) // 2
            if m > modend:
                for a, b, v in queries[modend:m]:
                    mods[a].append(v)
                    mods[b + 1].append(-v)
            elif m < modend:
                for a, b, v in queries[m:modend]:
                    mods[a].remove(v)
                    mods[b + 1].remove(-v)
            modend = m
            if check(nums, mods):
                r = m
            else:
                l = m + 1
        if l > len(queries):
            return -1
        return l

