    public Integer dp[][];
    // for each number, if I do a dp to find small index it becomes 0 at;
    public int minZeroArray(int[] nums, int[][] queries) {
        int max = Integer.MIN_VALUE;

        for(int i=0;i<nums.length;i++) {
            dp = new Integer[1002][queries.length+1];
            int ans = solve(nums[i], i, queries, 0);
            // System.out.println(ans);
            max = Math.max(max, ans);
        }

        return max == Integer.MAX_VALUE ? -1 : max;

    }

    public int solve(int num, int numInd, int[][] queries, int i) {
        int n = queries.length;
        if(num == 0) return i; // at this i, num became 0;
        if(i>=n) return Integer.MAX_VALUE; // num never became 0;

        if(dp[num][i]!=null) return dp[num][i];

        int op1 = Integer.MAX_VALUE;
        int op2 = Integer.MAX_VALUE;

        int left = queries[i][0];
        int right = queries[i][1];

        if(numInd>=left && numInd<=right && num>=queries[i][2]) {
            op1 = solve(num-queries[i][2], numInd, queries, i+1);
        }

        op2 = solve(num, numInd, queries, i+1);

        return dp[num][i] = Math.min(op1, op2);

    }
}

