class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0: return 0
        nums = [a for a in nums if a]
        n, nq = len(nums), len(queries)
        def can_get_sum(s, vals):
            dp = [False] * (s + 1)
            dp[0] = True
            for v in vals:
                next_dp = dp[:]
                for j in range(1, s + 1):
                    if v <= j and dp[j - v]: next_dp[j] = True
                dp = next_dp
                if dp[-1]: return True
            return False
        def can(k):
            for i in range(n):
                vals = []
                for l, r, val in queries[:k]:
                    if l <= i <= r and val <= nums[i]: vals.append(val)
                if not can_get_sum(nums[i], vals): return False
            return True
        if not can(nq): return -1
        l, r = 1, nq
        while l < r:
            m = (l + r) // 2
            if can(m):
                r = m
            else:
                l = m + 1
        return l

