class Solution {
public:
    bool func(int i , int ind , int val , int mid , vector<vector<int>> &brr){
        if(i == mid + 1) return (val == 0);
        if(dp[i][val] != -1) return dp[i][val];
        bool ans = 0;
        if(ind >= brr[i][0] && ind <= brr[i][1]){
            if(val >= brr[i][2]) ans |= func(i + 1 , ind , val - brr[i][2] , mid , brr);
        }
        ans |= func(i + 1 , ind , val , mid , brr);
        return dp[i][val] = ans;
    }
    bool is_valid(int mid , vector<int> &arr , vector<vector<int>> &brr){
        bool ans = 1;
        for(int i = 0 ; i < arr.size() ; i++){
            if(!arr[i]) continue;
            for(int j = 0 ; j <= mid + 3 ; j++){
                for(int k = 0 ; k <= arr[i] ; k++) dp[j][k] = -1;
            }
            ans &= func(0 , i , arr[i] , mid , brr);
        }
        return ans;
    }
    int minZeroArray(vector<int>& arr , vector<vector<int>>& q) {
        bool flag = 0;
        for(auto p : arr) if(p) flag = 1;
        if(!flag) return 0;
        int n = arr.size() , sz = q.size();
        int high = sz - 1 , low = 0;
        while(high - low > 1){
            int mid = (high + low) >> 1;
            if(is_valid(mid , arr , q)) high = mid;
            else low = mid;
        }
        if(is_valid(low , arr , q)) return low + 1;
        else if(is_valid(high , arr , q)) return high + 1;
        return -1;
    }
};

