class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        bool alreadyZero = true;
        for (int v : nums) {
            if (v != 0) { 
                alreadyZero = false; 
                break;
            }
        }
        if (alreadyZero) return 0;

        vector<vector<pair<int,int>>> canReduce(n);
        for (int k = 0; k < queries.size(); k++) {
            int l = queries[k][0], r = queries[k][1], val = queries[k][2];
            for (int i = l; i <= r; i++) {
                canReduce[i].push_back({val, k + 1});
            }
        }

        int ans = 0;
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            bitset<1001> dp;
            dp.reset();
            dp[0] = 1;
            int minK = -1;  
            for (auto &p : canReduce[i]) {
                int decrement = p.first;
                int qno = p.second;
                dp |= (dp << decrement);
                if (dp[target]) {
                    minK = qno;
                    break;
                }
            }
            if (minK == -1) return -1;
            ans = max(ans, minK);
        }
        return ans;
    }
};

