class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<int> max_i(n, -1);
        bool possible = true;

        for (int j = 0; j < n; ++j) {
            int target = nums[j];
            if (target == 0) {
                max_i[j] = -1;
                continue;
            }

            vector<pair<int, int>> j_queries;
            for (int i = 0; i < queries.size(); ++i) {
                int li = queries[i][0];
                int ri = queries[i][1];
                if (li <= j && j <= ri) {
                    j_queries.emplace_back(i, queries[i][2]);
                }
            }

            sort(j_queries.begin(), j_queries.end());

            unordered_map<int, int> dp;
            dp[0] = -1;

            for (auto& [idx, val] : j_queries) {
                unordered_map<int, int> tmp;
                for (auto& [s, m] : dp) {
                    int new_sum = s + val;
                    if (new_sum > target) continue;
                    int new_max = max(m, idx);
                    if (tmp.find(new_sum) == tmp.end() || new_max < tmp[new_sum]) {
                        tmp[new_sum] = new_max;
                    }
                }
                for (auto& [s, m] : tmp) {
                    if (dp.find(s) == dp.end() || m < dp[s]) {
                        dp[s] = m;
                    }
                }
            }

            if (dp.find(target) == dp.end()) {
                possible = false;
            } else {
                max_i[j] = dp[target];
            }
        }

        if (!possible) {
            return -1;
        }

        int ans = 0;
        for (int j = 0; j < n; ++j) {
            if (nums[j] != 0) {
                ans = max(ans, max_i[j] + 1);
            }
        }

        return ans;
    }
};

