class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        cache = {}
        def dp(index, value, i):
            key = (index, value)
            if key in cache:
                return cache[key]
            if value == 0:
                cache[key] = index
                return index
            if index == len(queries):
                cache[key] = index+1
                return index+1
            if not (queries[index][0] <= i <= queries[index][1]):
                return dp(index+1, value, i)
            if queries[index][2] > value:
                return dp(index+1, value, i)
            val = min(dp(index+1, value-queries[index][2], i), dp(index+1, value, i))
            cache[key] = val
            return val

        result = 0

        for i, n in enumerate(nums):
            cache = {}
            if n == 0:
                result = max(result, 0)
            else:
                result = max(result, dp(0, n, i))
        if result >= len(queries)+1:
            return -1
        return result

