class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        max_k = 0
        n = len(nums)
        m = len(queries)

        for i in range(n):
            num = nums[i]
            if num == 0:
                current_k = 0
            else:
                # Collect all queries that include the current index i
                Q = []
                for j in range(m):
                    l, r, val = queries[j]
                    if l <= i <= r:
                        Q.append((j, val))

                if not Q:
                    return -1

                # Subset sum dynamic programming
                dp = {0: -1}
                target = num
                for (j, val) in Q:
                    current_sums = list(dp.keys())
                    for s in current_sums:
                        new_sum = s + val
                        if new_sum not in dp or j < dp.get(new_sum, m):
                            dp[new_sum] = j

                if target not in dp:
                    return -1
                current_k = dp[target] + 1

            max_k = max(max_k, current_k)

        return max_k if max_k <= m else -1

