class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int ans = -1;
        int cnt = 0;
        for (auto val : nums) {
            int q = queries.size();
            int dp[q + 1][val + 1];
            memset(dp, 0, sizeof(dp));

            for (int i = 0; i <= q; i++) {
                dp[i][0] = 1;
            }
            for (int i = 1; i <= q; i++) {
                for (int j = 1; j <= val; j++) {
                    dp[i][j] = dp[i - 1][j];
                    if (j >= queries[i - 1][2] && cnt >= queries[i - 1][0] &&
                        cnt <= queries[i - 1][1]) {
                        dp[i][j] |= dp[i - 1][j - queries[i - 1][2]];
                    }
                    //cout<<i<<" "<<j<<" "<<dp[i][j]<<endl;
                }
            }
            int done = 0;
            for (int i = 0; i <= q; i++) {
                if (dp[i][val] == 1) {
                    ans = max(ans, i);
                    done = 1;
                    break;
                }
            }
            if (!done) {
                return -1;
            }
            cnt++;
        }
        return ans;
    }
};

