class Solution:
    def minZeroArray(self, a: List[int], q: List[List[int]]) -> int:
        n = len(a)
        s = [0] * n
        p = [1 << 0 for _ in range(n)]  

        if all(x == 0 for x in a):
            return 0

        for i, (l, r, v) in enumerate(q, 1):  
            for j in range(l, r + 1):
                s[j] += v
                p[j] |= p[j] << v  

            f = True
            for j in range(n):
                if s[j] < a[j] or (p[j] & (1 << a[j])) == 0:
                    f = False
                    break
            if f:
                return i

        f = True
        for j in range(n):
            if s[j] < a[j] or (p[j] & (1 << a[j])) == 0:
                f = False
                break
        return len(q) if f else -1

