class Solution:
    def subset_sum(self, arr, target):
        dp_prev = [False]*(target+1)
        dp_prev[0] = True
        for x in arr:
            dp_next = list(dp_prev)
            for v in range(target+1):
                if not dp_prev[v] or v+x > target:
                    continue
                dp_next[v+x] = True
            dp_prev = dp_next

        return dp_prev[target]

    def is_possible(self, nums, queries):
        index_list = [[] for _ in range(len(nums))]
        for l,r,val in queries:
            for i in range(l, r+1):
                index_list[i].append(val)

        for i in range(len(nums)):
            if not self.subset_sum(index_list[i], nums[i]):
                return False
        return True

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        left = 0
        right = len(queries)
        min_k = None
        while left <= right:
            mid = (left+right)//2
            q_prefix = queries[0:mid]
            if self.is_possible(nums, q_prefix):
                right = mid-1
                min_k = mid
            else:
                left = mid+1
        return min_k if min_k is not None else -1

