class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        auto allZero = [&]() {
            return all_of(nums.begin(), nums.end(), [](int x) { return x == 0; });
        };
        if (allZero()) return 0;

        vector<bitset<1001>> dp(n, bitset<1001>().set(0));

        for (int k = 0; k < queries.size(); k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];
            for (int j = l; j <= r; j++) {
                dp[j] |= (dp[j] << v);
                for (int pos = nums[j] + 1; pos < 1001; pos++) dp[j].reset(pos);
            }

            auto allAchieved = [&]() {
                for (int j = 0; j < n; j++) {
                    if (!dp[j].test(nums[j])) return false;
                }
                return true;
            };

            if (allAchieved()) return k + 1;
        }
        return -1;
    }
};

