            return 0
        ps = [{0} for _ in range(N)]
        for ind, (s, e, inc) in enumerate(queries):
            for pos in range(s, e + 1):
                ns = {cur + inc for cur in ps[pos] if cur + inc <= nums[pos]}
                ps[pos].update(ns)
            if all(nums[i] in ps[i] for i in range(N)):
                return ind + 1
        return -1

