class Solution {
        List<int[]>[] posQueries;

        public int minZeroArray(int[] nums, int[][] queries) {
            int n = nums.length, q = queries.length;
            boolean alreadyZero = true;
            for (int x : nums) {
                if (x != 0) {
                    alreadyZero = false;
                    break;
                }
            }
            if (alreadyZero) return 0;

            posQueries = new ArrayList[n];
            for (int i = 0; i < n; i++) {
                posQueries[i] = new ArrayList<>();
            }
            for (int j = 0; j < q; j++) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                for (int i = l; i <= r; i++) {
                    posQueries[i].add(new int[]{j, val});
                }
            }

            int low = 0, high = q, ans = -1;
            while (low <= high) {
                int mid = (low + high) / 2;
                if (checkIsTrue(mid, nums)) {
                    ans = mid;
                    high = mid - 1;
                } else {
                    low = mid + 1;
                }
            }
            return ans;
        }

        private boolean checkIsTrue(int k, int[] nums) {
            int n = nums.length;
            for (int i = 0; i < n; i++) {
                if (nums[i] == 0) continue;
                int target = nums[i];
                boolean[] dp = new boolean[target + 1];
                dp[0] = true;
                for (int[] pair : posQueries[i]) {
                    if (pair[0] >= k) break;
                    int v = pair[1];
                    for (int t = target; t >= v; t--) {
                        if (dp[t - v]) {
                            dp[t] = true;
                        }
                    }
                }
                if (!dp[target]) return false;
            }
            return true;
        }
    }

