        def solve(idx, x):
            if x == 0:
                return 0
            A = []
            for i, (l, r, v) in enumerate(queries):
                if l <= idx <= r:
                    A.append((v, i))
            if not A:
                return -1
            sm = set([0])
            for v, i in A:
                nsm = set()
                for s in sm:
                    if s + v == x:
                        return i + 1
                    nsm.add(s + v)
                sm |= nsm
            return -1

        ans = -1
        for i, x in enumerate(nums):
            cur = solve(i, x)
            if cur == -1:
                return -1
            ans = max(ans, cur)
        return ans

