public class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, q = queries.length;


        boolean alreadyZero = true;
        for (int num : nums) {
            if (num != 0) {
                alreadyZero = false;
                break;
            }
        }
        if (alreadyZero) return 0;


        List<Integer>[] affects = new ArrayList[n];
        for (int i = 0; i < n; i++) {
            affects[i] = new ArrayList<>();
        }
        for (int j = 0; j < q; j++) {
            int l = queries[j][0], r = queries[j][1];
            // For every index in [l, r], add query j.
            for (int i = l; i <= r; i++) {
                affects[i].add(j);
            }
        }

        // Binary search over the prefix length k of queries.
        int lo = 1, hi = q + 1; // hi = q+1 means "not possible" if we exceed q.
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if (canZero(nums, queries, affects, mid)) {
                hi = mid;
            } else {
                lo = mid + 1;
            }
        }
        return lo <= q ? lo : -1;
    }


    private boolean canZero(int[] nums, int[][] queries, List<Integer>[] affects, int k) {
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            int target = nums[i];

            if (target == 0) continue;

            List<Integer> coins = new ArrayList<>();

            for (int j : affects[i]) {
                if (j < k) {
                    coins.add(queries[j][2]);
                }
            }

            if (!subsetSum(coins, target)) {
                return false;
            }
        }
        return true;
    }

    private boolean subsetSum(List<Integer> coins, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for (int coin : coins) {
            for (int s = target; s >= coin; s--) {
                if (dp[s - coin]) {
                    dp[s] = true;
                }
            }
        }
        return dp[target];
    }
}

