class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int left = 0, right = m;

        auto eval = [&](vector<int>& arr, int& num) -> bool {
            int n = arr.size();
            vector<vector<bool>> dp(n + 1, vector<bool>(num + 1));
            for (int j = 0; j <= num; ++j) dp[n][j] = false;
            for (int i = 0; i <= n; ++i) dp[i][num] = true;

            for (int i = n - 1; i >= 0; --i) {
                for (int j = num - 1; j >= 0; --j) {
                    dp[i][j] = dp[i + 1][j];
                    if (arr[i] + j <= num) dp[i][j] = dp[i][j] || dp[i + 1][j + arr[i]];
                }
            }
            return dp[0][0];
        };

        auto go = [&](int k) -> bool {
            vector<int> arr = nums;
            vector<vector<int>> choices(n);
            for (int i = 0; i < k; ++i) {
                for (int j = queries[i][0]; j <= queries[i][1]; ++j) {
                    choices[j].push_back(queries[i][2]);
                }
            }
            for (int i = 0; i < n; ++i) {
                if (!eval(choices[i], nums[i])) return false;
            }
            return true;
        };

        while (left != right) {
            int mid = left + ((right - left) >> 1);
            if (go(mid)) right = mid;
            else left = mid + 1;
        }
        return go(left) ? left : -1;
    }
};
/*
(i, j)
j == num: return true
j > num || i == n: return false

dp(i, j) = dp(i + 1, j + arr[i]) || dp(i + 1, j)

dp[i][j] = false, for all j where i = n
dp[i][j] = true, for all i where j = num

dp[i][j] = dp[i + 1][j]
if arr[i] + j <= num:
    dp[i][j] = dp[i][j] || dp[i + 1][j + num]

*/

