import numpy as np
from typing import List
import bisect
class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)
        ode = [(l, r, v) for l, r, v in queries]
        fill = [[] for _ in range(n)]
        for idx, (l, r, dec) in enumerate(ode):
            for pos in range(l, r + 1):
                fill[pos].append((idx, dec))
        for i in range(n):
            fill[i].sort(key=lambda x: x[0])
        def possible(adha: int) -> bool:
            for pos in range(n):
                target = nums[pos]
                if target == 0:
                    continue
                idx = bisect.bisect_left(fill[pos], (adha, ))
                arr = [d for _, d in fill[pos][:idx] if d <= target]
                dp = np.zeros(target + 1, dtype=bool)
                dp[0] = True
                for d in arr:
                    shift = np.concatenate((np.zeros(d, dtype=bool), dp[:-d]))
                    dp |= shift
                    if dp[target]:
                        break
                if not dp[target]:
                    return False
            return True
        a,mate= 0, m + 1
        while a < mate:
            adha = (a + mate) // 2
            if possible(adha):
                mate= adha
            else:
                a = adha + 1
        return a if a <= m else -1

