class Solution {
    public int minZeroArray(int[] nums, int[][] q) {
        int n = nums.length;
        int m = q.length;
        boolean f = true;
        for(int x:nums) if(x!=0) f = false;
        if(f) return 0;
        int st = 1;
        int end = m;
        int ans = -1;

        while(st<=end){
            int mid = st+(end-st)/2;
            if(possible(nums,q,mid)){
                ans = mid;
                end = mid-1;
            }
            else st = mid+1;
        }

        return ans;
    }

    public boolean possible(int[] a, int[][] q, int lim){
        int n = a.length;
        List<Integer>[] l = new ArrayList[n];
        for(int i=0;i<n;i++) l[i] = new ArrayList<>();
        for(int i=0;i<lim;i++){
            int[] t = q[i];
            int st = t[0];
            int end = t[1];
            l[st].add(t[2]);
            if(end+1<n) l[end+1].add(-1*t[2]);
        }
        Map<Integer,Integer> mp = new HashMap<>();
        for(int i=0;i<n;i++){
            // System.out.println(l[i]);
            if(l[i].size()>0){
                for(int x:l[i]){
                    if(x>0) mp.put(x,mp.getOrDefault(x,0)+1);
                    else{
                        // if(!mp.containsKey(x)) System.out.println(i + " " + x + " " + lim);
                        mp.put(-1*x, mp.get(-1*x)-1);
                        if(mp.get(-1*x)==0) mp.remove(-1*x);
                    }
                }
            }
            List<Integer> list = new ArrayList<>();
            for(int x:mp.keySet()){
                int val = mp.get(x);
                for(int j=0;j<val;j++) list.add(x);
            }
            Boolean[][] dp = new Boolean[a[i]+1][list.size()+1];

            boolean f = helper(a[i],list,0,dp);
            if(!f) return false;
        }
        return true;
    }

    public boolean helper(int x, List<Integer> l, int i, Boolean[][] dp){
        if(i>=l.size()) return x==0;
        if(dp[x][i]!=null) return dp[x][i];
        boolean f = helper(x,l,i+1,dp);
        if(l.get(i)<=x) f = f || helper(x-l.get(i),l,i+1,dp);

        return dp[x][i] = f;
    }
}

