class Solution {
public:
    bool checkForSubsetSumPossible(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            vector<int> contributions;
            for (int j = 0; j <= k; j++) {
                int l = queries[j][0], r = queries[j][1], x = queries[j][2];
                if (i >= l && i <= r) {
                    contributions.push_back(x);
                }
            }
            int target = nums[i];
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int x : contributions) {
                for (int s = target; s >= x; s--) {
                    if (dp[s - x])
                        dp[s] = true;
                }
            }
            if (!dp[target])
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<vector<int>> temp = queries; 

        bool allZeros = true;
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0) {
                allZeros = false;
                break;
            }
        }
        if (allZeros)
            return 0;

        int low = 0, high = q - 1, ans = -1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (checkForSubsetSumPossible(nums, queries, mid)) {
                ans = mid + 1;  
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }

        return ans;
    }
};

