        int q=queries.size(),n=nums.size();
        for(int i=0;i<q;i++) for(int j=0;j<n;j++) for(int k=0;k<=1000;k++) dp[i][j][k]=-1;
        int c=0;
        for(int i=0;i<n;i++) if(nums[i]==0) c++;
        if(c==n) return 0;
        for(int i=q-1;i>=0;i--) for(int j=0;j<n;j++) rec(i,j,nums[j],nums,queries);
        for(int i=0;i<q;i++){
            int flag=1;
            for(int j=0;j<n;j++) if(dp[i][j][nums[j]]==0){
                flag=0;
                break;
            }
            if(flag==1) return i+1;
        }
        return -1;
    }
};

