/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function(nums, queries) {
    if (Math.max(...nums) === 0) return 0
    const N = nums.length
    const cands = Array(N).fill(0).map((x, i) => new Set([nums[i]]))
    for (let i = 0; i < queries.length; i++) {
        const [l, r, val] = queries[i]
        for (let j = l; j <= r; j++) {
            const arr = []
            for (const cand of cands[j]) {
                if (cand - val >= 0) arr.push(cand - val)
            }
            for (const c of arr) cands[j].add(c)
        }
        if (check()) return i + 1
    }
    return -1
    function check() {
        for (const cand of cands) {
            if (!cand.has(0)) return false
        }
        // console.log('check', cands)
        return true
    }
};

