bool find1(ll curr, ll pos, vl &arr, vector<vector<ll>> &dp, ll tar)
{
    if(curr > tar)
        return false;

    if(curr == tar)
        return true;

    if(pos == sz(arr))
        return false;

    if(dp[pos][curr] != -1)
        return dp[pos][curr];

    ll now = arr[pos];

    bool stat = false;

    bool a1 = find1(curr, pos+1, arr, dp, tar);
    bool a2 = find1(curr+now, pos+1, arr, dp, tar);

    stat |= a1;
    stat |= a2;

    return dp[pos][curr] = stat;
}


