        ll r = q[i][1];
        ll val = q[i][2];

        for(ll j=l; j<=r; ++j)
        {
            m[j].pb(val);
        }
    }

    for(ll i=0; i<sz(nums); ++i)
    {
        ll curr = nums[i];
        bool stat = check(curr, m[i]);

        if(!stat)
            return false;
    }

    return true;
}


class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {

        ll low = 0;
        ll high = sz(q)-1;

        ll ans = -1;

        ll cnt = 0;

        for(ll i=0; i<sz(nums); ++i)
        {
            if(nums[i] == 0)
                ++cnt;
        }

        if(cnt == sz(nums))
            return 0;

        while(low <= high)
        {
            ll mid = (low + high)/2;
            bool stat = find(nums, q, mid);

            if(stat)
            {
                ans = mid;
                high = mid-1;
            }                

            else
                low = mid+1;
        }

        if(ans == -1)
            return -1;

        return ans+1;

    }
};

