from collections import defaultdict
class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        d=defaultdict(set)
        # each index has its own set and we just expand the set with the val, if at some point we find our nums[i] then we increase the counter and mark it as true
        a=0
        for x in nums:
            a+=x
        if a==0:
            return 0
        n=len(nums)
        c=0
        found=[False]*n
        for i in range(n):
            if nums[i]==0:
                found[i]=True
                c+=1

        for i in range(n):
            d[i]={0}
        for k,(l,r,val) in enumerate(queries):
            for i in range(l,r+1):
                if not found[i]:
                    cur_set=d[i]
                    new_set=set()
                    for x in cur_set:
                        new_set.add(x)
                    for x in cur_set:
                        if x+val==nums[i]:
                            c+=1
                            if c==n:
                                return k+1
                            found[i]=True
                        new_set.add(x+val)
                    d[i]=new_set
        return -1

