                for j in range(1001):
                    if dp[i][j]==1 or (j+val<=1000 and dp[i][j+val]==1):
                        dp[i][j]=1
            cnt0=0
            for i in range(n):
                if dp[i][0]==1: cnt0+=1
            if cnt0==n: return e+1
        return -1

