class Solution {
public:
    bool fun(vector<int>& a, vector<vector<int>>& q, int k) {
        int n = a.size();
        for (int i = 0; i < n; i++) {
            int el = a[i];
            bitset<1101> dp;
            dp[0] = 1;
            for (int j = 0; j < k; j++) {
                int l = q[j][0], r = q[j][1], val = q[j][2];
                if (l <= i && i <= r) {
                    dp |= (dp << val);
                }
            }
            if (!dp[el]) return 0;
        }
        return 1;
    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        int m = q.size();

        int f=1;
        for(int i=0;i<n;i++){
            if(a[i]!=0) f=0;
        }
        if(f) return 0;

        if (!fun(a, q, m))
            return -1;

        int l = 1, r = m;
        int ans=-1;
        while (l<=r) {
            int mid = l + (r - l) / 2;
            if (fun(a, q, mid)){
                ans=mid;
                r = mid-1;
            }
            else{
                l = mid + 1;
            }
        }
        return l;
    }
};

