class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int m = queries.length, low = 0, high = m + 1;
        while(low < high){
            int mid = low + (high - low) / 2;
            if(check(nums, queries, mid)) high = mid;
            else low = mid + 1;
        }
        return low <= m ? low : -1;
    }
    boolean check(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        for(int i = 0; i < n; i++){
            int target = nums[i];
            ArrayList<Integer> coins = new ArrayList<>();
            for(int j = 0; j < k; j++){
                if(queries[j][0] <= i && i <= queries[j][1]){
                    coins.add(queries[j][2]);
                }
            }
            if(!canAchieve(target, coins)) return false;
        }
        return true;
    }
    boolean canAchieve(int target, List<Integer> coins) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for(int coin : coins){
            if(coin > target) continue;
            for(int j = target; j >= coin; j--){
                if(dp[j - coin]) dp[j] = true;
            }
        }
        return dp[target];
    }
}

