class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = 0;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] == 0) continue;
            int r = helper(queries, i, nums[i]);
            if (r == -1) return -1;
            res = max(res, r);
        }
        return res;
    }
    int helper(vector<vector<int>>& q, int i, int v) {
        vector<int> seen(1001, 0);
        seen[0] = 1;
        for (int j = 0; j < q.size(); ++j) {
            if (q[j][0] <= i && q[j][1] >= i) {
                int val = q[j][2];
                set<int> add;
                for (int k = 0; k <= v - val; k++) {
                    if (seen[k]) 
                        add.insert(k + val);
                }
                for (int k : add)
                    seen[k] = 1;
                if (seen[v]) return j + 1;
            }
        }
        return -1;
    }
};

