class Solution {
    public int minZeroArray(int[] nums, int[][] qs) {
        int n = nums.length, m = qs.length;
        boolean all0 = true;
        for (int x : nums) {
            if(x != 0) { all0 = false; break; }
        }
        if(all0) return 0;
        for (int k = 1; k <= m; k++) {
            boolean ok = true;
            for (int j = 0; j < n; j++) {
                int tar = nums[j];
                if(tar == 0) continue;
                boolean[] dp = new boolean[tar + 1];
                dp[0] = true;
                for (int i = 0; i < k; i++) {
                    int l = qs[i][0], r = qs[i][1], v = qs[i][2];
                    if(j >= l && j <= r) {
                        for (int t = tar - v; t >= 0; t--) {
                            if(dp[t]) dp[t + v] = true;
                        }
                    }
                }
                if(!dp[tar]) { ok = false; break; }
            }
            if(ok) return k;
        }
        return -1;
    }
}

