class Solution {
public:
    // finds the maximum amount of queries needed to be taken
    vector<vector<int>> dp;
    vector<vector<int>> q;
    int minZeroArray(vector<int>& v, vector<vector<int>>& queries) {
        q = queries;
        // for each index in v run a dp memo
        // take max or return -1 if invalid

        int ans = 0;
        int i = 0;
        for (auto &n : v){
            dp = vector<vector<int>>(1001, vector<int>(q.size(), -2));
            int next = dfs(n, 0, i);
            ++i;
            if (next == -1) return -1;
            ans = max(ans, next);
        }
        return ans;
    }

    int dfs(int n, int i, int r){
        if (n == 0) return 0;
        if (n < 0) return -1;
        if (i == q.size()) return -1;
        if (dp[n][i] == -2){
            // choose to skip
            int skip = dfs(n, i + 1, r);
            if (skip != -1) skip += 1;
            int ans = skip;
            // choose to take if possible
            int take = -1;
            if (r >= q[i][0] && r <= q[i][1])
                take = dfs(n - q[i][2], i + 1, r);
            if (take != -1) take += 1;
            if (ans == -1) ans = take;
            else if (take != -1) ans = min(ans, take);
            dp[n][i] = ans;
        }
        return dp[n][i];
    }
};

