class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        bool alreadyZero = true;
        for(int x: nums) {
            if(x != 0) {
                alreadyZero = false;
                break;
            }
        }
        if(alreadyZero) return 0;
        vector<vector<bool>> dp(n, vector<bool>(1001, false));
        for(int j = 0; j < n; j++){
            dp[j][0] = true;
        }
        for(int i = 0; i < m; i++){
            int start = queries[i][0];
            int end = queries[i][1];
            int dec = queries[i][2];
            for(int j = start; j <= end; j++){
                for(int s = 1000; s >= dec; s--){
                    if(dp[j][s - dec]){
                        dp[j][s] = true;
                    }
                }
            }
            bool allPossible = true;
            for(int j = 0; j < n; j++){
                int need = nums[j];
                if(need <= 1000){
                    if(!dp[j][need]){
                        allPossible = false;
                        break;
                    }
                } else {
                    allPossible = false;
                    break;
                }
            }
            if(allPossible){
                return i + 1;
            }
        }
        return -1;
    }
};

