class Solution {
    public:
        int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
            int q = queries.size(), n = nums.size();

            auto isSumPoss = [&](vector<int> v, int sum) {
                int n = v.size();
                vector<vector<int>> dp(n + 1, vector<int>(sum + 1, 0));
                dp[0][0] = 1;

                for (int i = 1; i <= n; ++i) {
                    for (int j = 0; j <= sum; ++j) {
                        dp[i][j] = dp[i - 1][j];
                        if (j >= v[i - 1]) {
                            dp[i][j] |= dp[i - 1][j - v[i - 1]];
                        }
                    }
                }
                return dp[n][sum];
            };

            auto check = [&](int k) {
                map<int, vector<int>> mp;
                for (int i = 0; i < k; ++i) {
                    for (int j = queries[i][0]; j <= queries[i][1]; ++j) {
                        mp[j].push_back(queries[i][2]);
                    }
                }
                for (int i = 0; i < n; ++i) {
                    vector<int> v = mp[i];
                    // for (int x : v) cout << x << 'n';
                    if (!isSumPoss(v, nums[i])) {
                        return false;
                    }
                }

                return true;
            };

            int lo = 0, hi = q;
            while (lo <= hi) {
                int mid = lo + (hi - lo) / 2;
                if (check(mid)) {
                    hi = mid - 1;
                } else {
                    lo = mid + 1;
                }
            }

            // for (int i = 0; i <= q; ++i) cout << check(i) << 'n';

            return (lo <= q ? lo : -1);
        }
    };

