class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        vector<vector<bool>> dp(n, vector<bool>(1001, false));
        for (int j = 0; j < n; j++) {
            dp[j][0] = true;  
        }
        bool is_zero = true;
        for (int j = 0; j < n; j++) {
            if (nums[j] != 0) {
                is_zero = false;
                break;
            }
        }
        if (is_zero) return 0;

        for (int i = 0; i < queries.size(); i++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for (int j = l; j <= r; j++) {
                for (int s = nums[j]; s >= v; s--) {
                    if (!dp[j][s] && dp[j][s - v]) {
                        dp[j][s] = true;
                    }
                }
            }

            bool all_zero = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j][nums[j]]) {
                    all_zero = false;
                    break;
                }
            }
            if (all_zero) return i + 1;
        }

        return -1;
    }
};

