    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<bool>> left(n,vector<bool>(1001,false));
        int max = *std::max_element(nums.begin(), nums.end());
        if(max==0) return 0;
        for(int i =0;i<n;i++){
            left[i][nums[i]] = true;
        }
        for(int j = 0; j < queries.size();j++)
        {
            for(int i = queries[j][0]; i<= queries[j][1];i++){
                if(left[i][0]) continue;
                for(int k = queries[j][2];k <= 1000;k++){
                    if(left[i][k])
                        left[i][k-queries[j][2]] = true;
                }
            }
            bool res = true;    
            for(int i =0;i<n;i++){
                res &= left[i][0];
            }
            if(res) return j+1;
        }
        return -1;
    }
};

