public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<bool>done(n, false);
        bool f=true;
        for(int i=0;i<n;i++){
            if(nums[i]==0) done[i]=true;
            else f=false;
        }
        if(f) return 0;

        for(int i=0;i<queries.size();i++){
            bool flag=true;
            for(int j=0;j<n;j++){
                if(done[j]) continue;
                vector<int>v;
                for(int k=0;k<=i;k++){
                    if(j>=queries[k][0] && j<=queries[k][1]){
                        v.push_back(queries[k][2]);
                    }
                }
                vector<bool>dp(nums[j]+1, false);
                dp[0] = true;
                for(auto it:v){
                    for(int l=nums[j];l>=it;l--){
                        if(dp[l-it]) dp[l]=true;
                    }
                }
                if(!dp[nums[j]]){
                    flag=false;
                    break;
                }
                else done[j]=true;
            }
            if(flag) return i+1;
        }
        return -1;
    }
};

