class Solution {
public:
    bool cans(int t, const vector<int>& d) {
        int n = d.size();
        vector<bool> dp(t + 1, false);
        dp[0] = true;

        for (int num : d) {
            for (int j = t; j >= num; --j) {
                dp[j] = dp[j] || dp[j - num];
            }
        }

        return dp[t];
    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        set<int> s(a.begin(), a.end());
        if (s.size() == 1 && *s.begin() == 0) return 0; 

        int l = 0, r = q.size(), ans = -1;

        while (l <= r) {
            int m = (l + r) / 2;
            vector<vector<int>> d(n);

            for (int i = 0; i < m; ++i) {
                for (int j = q[i][0]; j <= q[i][1]; ++j) {
                    d[j].push_back(q[i][2]);
                }
            }

            bool f = true;
            for (int i = 0; i < n; ++i) {
                if (a[i] != 0 && !cans(a[i], d[i])) { 
                    f = false;
                    break;
                }
            }

            if (f) {
                ans = m;
                r = m - 1;
            } else {
                l = m + 1;
            }
        }

        return ans;
    }
};

