class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        int m = q.size();

        auto check = [&](int x, vector<int> &v) {
            vector<int> dp(x + 1);
            dp[0] = 1;
            for (int i: v) {
                for (int j = x; j >= 0; j--) {
                    if (dp[j] && j + i <= x) {
                        dp[j + i] = 1;
                    }
                }
            }
            return dp[x];
        };

        auto process = [&](int k) {
            vector<vector<int>> b(n);
            for (int i = 0; i < k; i++) {
                int l = q[i][0], r = q[i][1], val = q[i][2];
                for (int j = l; j <= r; j++) {
                    b[j].push_back(val);
                }
            }
            bool res = true;
            for (int i = 0; i < n; i++) {
                res &= check(a[i], b[i]);
            }
            return res;
        };

        int l = 0, r = m, res = -1;
        while (l <= r) {
            int mid = (l + r) >> 1;
            if (process(mid)) {
                res = mid;
                r = mid - 1;
            }
            else l = mid + 1;
        }
        return res;
    }
};

