#define ll long long
class Solution {
private:
    bool isZeroArray(const vector<int>& nums) {
        return all_of(nums.begin(), nums.end(), [](int x) { return x == 0; });
    }
    int helper(vector<int> nums, vector<vector<int>> queries) {
        ll n = nums.size();
        ll m = queries.size();

        if (isZeroArray(nums))
            return 0;

        const ll MAX_SUM = 1100;

        auto isPossible = [&](ll k) -> bool {
            for (ll i = 0; i < n; i++) {
                ll target = nums[i];
                bitset<MAX_SUM> dp;
                dp.reset();
                dp.set(0, true);

                for (ll j = 0; j < k; j++) {
                    ll left = queries[j][0], right = queries[j][1],
                        value = queries[j][2];
                    if (left <= i && i <= right) {
                        dp |= (dp << value);
                    }
                }
                if (!dp.test(target))
                    return false;
            }
            return true;
        };

        ll low = 0, high = m + 1, ans = -1;
        while (low < high) {
            ll mid = low + (high - low) / 2;
            if (isPossible(mid)) {
                ans = mid;
                high = mid;
            } else {
                low = mid + 1;
            }
        }

        return (int)ans;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        return helper(nums, queries);
    }
};

