class Solution {
public:
    bool canSumToTarget(const vector<int>& nums, int target) {
        // Create a dp array of size target + 1, initialized to false
        vector<bool> dp(target + 1, false);

        // A sum of 0 is always achievable (using an empty subset)
        dp[0] = true;

        // Iterate over each number in the array
        for (int num : nums) {
            // Traverse the dp array from right to left to avoid using the same number multiple times
            for (int i = target; i >= num; --i) {
                if (dp[i - num]) {
                    dp[i] = true;
                }
            }
        }

        // Return whether dp[target] is true (i.e., whether the target can be achieved)
        return dp[target];
    }

    bool check(vector<int> &nums, vector<vector<int>> &op) {
        bool ok = true;
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            ok &= canSumToTarget(op[i], nums[i]);
        }
        return ok;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        auto &A = nums;
        auto &Q = queries;
        int n = nums.size();
        int q = Q.size();

        vector<vector<int>> options(n);
        if (check(nums, options)) {
            return 0;
        }

        int l = 0, r = q;
        int ans = -1;
        while (l <= r) {
            int m = (l + r) / 2;
            options.assign(n, {});
            for (int i = 0; i < m; i++) {
                for (int k = Q[i][0]; k <= Q[i][1]; k++) {
                    options[k].push_back(Q[i][2]);
                }
            }
            if (check(nums, options)) {
                ans = m;
                r = m - 1;
            }
            else {
                l = m + 1;
            }
        }
        return ans;

        for (int i = 0; i < q; i++) {
            for (int k = Q[i][0]; k <= Q[i][1]; k++) {
                options[k].push_back(Q[i][2]);
            }
            if (check(nums, options)) {
                return i + 1;
            }
        }
        return -1;
    }
};

